import React, {useEffect, useState} from "react";
import {MatrixBlock, MatrixColumn, MatrixRow, MatrixWrapper} from "./MatrixStyles";
import {useDispatch, useSelector} from "react-redux";
import {AppRootStateType} from "../../state/store";
import {
    createMatrixAC,
    MatrixType,
    PointType,
    PossibleMovesType,
    setMatrixSizeAC,
    setStartCoordinatesAC,
} from "../../state/reducer";

const getMovesAmount = (matrixSize: number) => {
    const movesAmount = 10 + (matrixSize - 3) * 5;
    return Math.max(10, movesAmount); // minimum 10 turns
};

const getNextMove = (startPosition: PointType, matrixSize: number) => {
    const possibleMoves = [];
    if (startPosition.row !== 0) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveUp',
            row: startPosition.row - 1,
        });
    }
    if (startPosition.row !== matrixSize - 1) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveDown',
            row: startPosition.row + 1,
        });
    }
    if (startPosition.column !== 0) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveLeft',
            col: startPosition.column - 1,
        });
    }
    if (startPosition.column !== matrixSize - 1) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveRight',
            col: startPosition.column + 1,
        });
    }

    return possibleMoves[Math.round(Math.random() * (possibleMoves.length - 1))];
};


const getPossibleMoves = (
    startCoordinates: PointType,
    matrixSize: number,
    movesLeft: number,
    moves: Array<string> = []):
    { endPosition: PointType; moves: Array<string> } => {
    if (movesLeft > 0) {
        const {dir, column, row} = getNextMove(startCoordinates, matrixSize);
        return getPossibleMoves({dir, column, row}, matrixSize, movesLeft - 1, [
            ...moves,
            dir,
        ]);
    } else {
        return {moves, endPosition: startCoordinates};
    }
};


export const Matrix = () => {
    const dispatch = useDispatch()
    const matrix = useSelector<AppRootStateType, MatrixType>(state => state.matrix.matrix)
    const matrixSize = useSelector<AppRootStateType, number>(state => state.matrix.matrixSize)
    const startCoordinates = useSelector<AppRootStateType, PointType>(state => state.matrix.startPoint)
    const endCoordinates = useSelector<AppRootStateType, PointType>(state => state.matrix.endPoint)
    const possibleMoves = useSelector<AppRootStateType,PossibleMovesType>(state => state.matrix.possibleMoves)
    const matrixSizeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        dispatch(setMatrixSizeAC(Number(e.currentTarget.value)))
    }
    const matrixStart = (matrixSize: number) => {
        dispatch(setStartCoordinatesAC(matrixSize))
        getPossibleMoves(startCoordinates, matrixSize, getMovesAmount(matrixSize))
    }

    // const matrixMove = (moves: number = 5) => {
    //     if (endCoordinates.row !== 0) {
    //         console.log(endCoordinates.row)
    //         console.log('1st if ', endCoordinates.row)
    //         setMovesArr(moveArr => ([...moveArr, 'moveUp']))
    //         // dispatch(moveUpAC())
    //     }
    //     if (endCoordinates.row !== matrix.length - 1) {
    //         console.log('2st if ', endCoordinates.row)
    //         setMovesArr(moveArr => ([...moveArr, 'moveDown']))
    //         // dispatch(moveDownAC())
    //     }
    //     if (endCoordinates.column !== 0) {
    //         console.log('3th if ', endCoordinates.column)
    //         setMovesArr(moveArr => ([...moveArr, 'moveLeft']))
    //         // dispatch(moveLeftAC())
    //     }
    //     if (endCoordinates.column !== matrix.length - 1) {
    //         console.log('4th if ', endCoordinates.column)
    //         setMovesArr(moveArr => ([...moveArr, 'moveRight']))
    //         // dispatch(moveRightAC())
    //     }
    // }
    useEffect(() => {
        dispatch(createMatrixAC(matrixSize))
    }, [matrixSize])
    console.log('MOVES',possibleMoves)
    return (
        <MatrixWrapper>
            <input type='number'
                   value={matrixSize}
                   onChange={matrixSizeHandler}/>
            <button onClick={() =>
                matrixStart(matrixSize)
                // setStartPosition(getRandomPoint(matrixSize))
            }>начать игру
            </button>
            <MatrixBlock>
                {
                    matrix.map((row, rowIndex) => {
                        return <MatrixRow key={rowIndex}>
                            {matrix[rowIndex].map((column, columnIndex) =>
                                <MatrixColumn
                                    rowIndex={rowIndex}
                                    startRow={startCoordinates.row}
                                    endRow={endCoordinates.row}
                                    startColumn={startCoordinates.column}
                                    endColumn={endCoordinates.column}
                                    columnIndex={columnIndex}
                                    key={columnIndex}
                                >
                                    {column}
                                </MatrixColumn>)}
                        </MatrixRow>
                    })
                }
            </MatrixBlock>
            <ol>
                {possibleMoves.map((move, i) => (
                    <li key={i}>{move}</li>
                ))}
            </ol>
        </MatrixWrapper>
    );
}


