import React, {useEffect, useState} from 'react';

const getRandomPoint = (matrixSize: number) => {
    const row = Math.round(Math.random() * (matrixSize - 1));
    const col = Math.round(Math.random() * (matrixSize - 1));
    return {row, col};
};

const getMovesAmount = (matrixSize: number) => {
    const movesAmount = 10 + (matrixSize - 3) * 5;
    return Math.max(10, movesAmount); // minimum 10 turns
};

const getNextMove = (startPosition: { dir: string | undefined, row: number, col: number }, matrixSize: number) => {
    const possibleMoves = [];
    if (startPosition.row !== 0) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveUp',
            row: startPosition.row - 1,
        });
    }
    if (startPosition.row !== matrixSize - 1) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveDown',
            row: startPosition.row + 1,
        });
    }
    if (startPosition.col !== 0) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveLeft',
            col: startPosition.col - 1,
        });
    }
    if (startPosition.col !== matrixSize - 1) {
        possibleMoves.push({
            ...startPosition,
            dir: 'moveRight',
            col: startPosition.col + 1,
        });
    }

    return possibleMoves[Math.round(Math.random() * (possibleMoves.length - 1))];
};


const getPossibleMoves = (startPosition:
                              { dir: string | undefined, row: number, col: number },
                          matrixSize: number,
                          movesLeft: number,
                          moves: Array<string> = []): { endPosition: { dir?: string | undefined; row: number; col: number }; moves: Array<string> } => {
    if (movesLeft > 0) {
        const {dir, col, row} = getNextMove(startPosition, matrixSize);
        return getPossibleMoves({dir, col, row}, matrixSize, movesLeft - 1, [
            ...moves,
            dir,
        ]);
    } else {
        return {moves, endPosition: startPosition};
    }
};

export const Matrix = () => {
    const [matrixSize, setMatrixSize] = useState(3);
    const [startPosition, setStartPosition] = useState<{ row: number; col: number; }>(
        getRandomPoint(matrixSize)
    );
    const [endPosition, setEndPosition] = useState<{ dir?: string, col: number, row: number }>({
        dir: '',
        col: 0,
        row: 0
    });
    const [possibleMoves, setPossibleMoves] = useState<Array<string>>([]);


    const matrixSizeHandler = (e: React.ChangeEvent<HTMLInputElement>) => {
        setMatrixSize(Number(e.currentTarget.value));
        setStartPosition(getRandomPoint(matrixSize)); //start a new game
    };

    const checkAnswer = ({col, row}: { col: number, row: number }) => {
        if (endPosition.col === col && endPosition.row === row) {
            alert('You won');
            setStartPosition(getRandomPoint(matrixSize)); //start a new game
        } else {
            alert('Looser');
        }
    };

    useEffect(() => {
        const {moves, endPosition} = getPossibleMoves(
            startPosition,
            matrixSize,
            getMovesAmount(matrixSize)
        );
        setEndPosition(endPosition);
        setPossibleMoves(moves);
    }, [startPosition]);

    return (
        <div>
            <input
                type="number"
                min={2}
                value={matrixSize}
                onChange={matrixSizeHandler}
            />
            <button onClick={() => setStartPosition(getRandomPoint(matrixSize))}>
                начать игру
            </button>
            <div>
                {Array.from(Array(matrixSize)).map((row, rowIndex) => {
                    return (
                        <div key={rowIndex} style={{display: 'flex'}}>
                            {Array.from(Array(matrixSize)).map((column, columnIndex) => (
                                <button
                                    key={columnIndex}
                                    onClick={() =>
                                        checkAnswer({col: columnIndex, row: rowIndex})
                                    }
                                    style={{
                                        // @ts-ignore
                                        color:
                                            rowIndex === startPosition.row &&
                                            columnIndex === startPosition.col &&
                                            'green',
                                    }}
                                >
                                    {columnIndex}
                                </button>
                            ))}
                        </div>
                    );
                })}
            </div>
            <ol>
                {possibleMoves.map((move) => (
                    <li>{move}</li>
                ))}
            </ol>
        </div>
    );
};

export default Matrix;
